			while( innerhtml.indexOf('&lt;i&gt;')+innerhtml.indexOf('&lt;/i&gt;') >= -1  ){ // while these tags are found
				innerhtml = innerhtml.replace('&lt;i&gt;', '<i>');
				innerhtml = innerhtml.replace('&lt;/i&gt;', '</i>');
			}
			domref.innerHTML = innerhtml;
