                this.mergeV2AnnotationLevel(oMetadata[this.oUI5ODataModelAnnotatableObject.objectName],
                        oAnnotation[this.oUI5ODataModelAnnotatableObject.objectName],
                        this.oUI5ODataModelAnnotatableObject);
