        oRm.write("<div class='sapUshellDynamicTileIndication'>");

        // state arrow

            if (oControl.getStateArrow()) {
                oRm.write("<div");
                oRm.addClass("sapUshellDynamicTileStateArrow");
                oRm.addClass("sapUshellDynamicTileData" + oControl.getStateArrow());
                oRm.writeClasses();
                oRm.write(">");
                oRm.write("</div>");
            }

            // unit
