                    var w = getAttrAsFloat(shapeEl, "width");
                    var h = getAttrAsFloat(shapeEl, "height");
                    var rx = getAttrAsFloat(shapeEl, "rx");
                    var ry = getAttrAsFloat(shapeEl, "ry");
