	migrateTags: function(K, mode, type) {
		mode = mode || 1;
		type = type || "IN";
		var TAGS = [];
		var tags = {};

		if(K.tags)
		{
			if(K.tags.length>0){
				var tagList = K.tags.split(",");
				for(var i=0,l=tagList.length; i<l; i++) {
					tags.ID=K.id;
					tags.IS_ACTIVE=mode;
					tags.TYPE=type;
					tags.TAG=tagList[i];
					TAGS.push(tags);
					tags = {};
				}
				return TAGS;
			}
		}
		return TAGS;
	},

